def get_lenght_of_missing_array(array_of_arrays):
    try:
        sorted_arr =  sorted(array_of_arrays, key=lambda x: len(x))
    except TypeError:
        return 0
    for pos, arr in enumerate(sorted_arr):
        print(len(arr), arr)
        if len(arr) == 0:
            return 0
        if (pos >= 1) and (len(arr) != len(sorted_arr[pos-1]) + 1):
            print(len(arr)-1)
            return len(arr) - 1
    return 0



